import React, { useState } from 'react';
import AnimationContainer from './components/AnimationContainer.js';
import Hero from './components/Hero.js';
import ReactRevelaMenu from './components/ReactRevelaMenu.js';

function App() {
  const animationDirections = ['top', 'right', 'bottom', 'left'];
  const [anmyType, setAnmyType] = useState('Roll');

  return (
    <div className="App">
      <Hero />
      <ReactRevelaMenu setAnmyType={setAnmyType}/>
      <AnimationContainer animationDirections={animationDirections}  animationType = {anmyType}/>
    </div>
  );
}

export default App;
