import React, { useEffect, useMemo, useState } from 'react';
import reactDom from 'react-dom';
import AnimationContainer from './components/AnimationContainer';
import AppNav from './components/AppNav';
import Hero from './components/Hero';
import ReactRevelaMenu from './components/ReactRevelaMenu';

function App() {

  const animationDirections = ['top', 'right', 'bottom', 'left'];
  const [anmyType, setAnmyType] = useState('Roll');
  const [rndElem, setRndElem] = useState(<><ReactRevelaMenu setAnmyType={setAnmyType}/>
    <AnimationContainer animationDirections={animationDirections}  animationType = {anmyType}/></>);

  useMemo(() => {
    // reactDom.render(rndElem, document.querySelector('.App'))    
    if( document.querySelector('.App') ){ console.log(document.querySelector('.App')); }
  }, []);

    useEffect(() => {
      reactDom.render(rndElem, document.querySelector('.App'))    
    }, [rndElem]);

  return (
    <div className="App">
      <Hero />
      {/* <AppNav /> */}
      <div className={'appNav'}>
            <span className={'appNav__item'} onClick={() => setRndElem( <>Skeleton</> )}> Skeleton </span>
            <span className={'appNav__item'} onClick={() => setRndElem( <>Both</> )}> Both </span>
            <span className={'appNav__item'} onClick={() => 
                setRndElem( <><ReactRevelaMenu setAnmyType={setAnmyType}/>
                    <AnimationContainer animationDirections={animationDirections}  animationType = {anmyType}/></> )
            }> React-Reveal </span>
      </div>        
      {/* <AppNav setRndElem={setRndElem} setAnmyType={setAnmyType} animationDirections={animationDirections}  animationType = {anmyType}/>
      {reactDom.render(rndElem, document.getElementById('root'))} */}
      {/* <ReactRevelaMenu setAnmyType={setAnmyType}/>
      <AnimationContainer animationDirections={animationDirections}  animationType = {anmyType}/> */}
    </div>
  );
}

export default App;
