import React, { useEffect, useState } from 'react';
import '../styles/Hero.css';

function Hero() {

    const [payloadVals, setPayloadVals] = useState(false);

    useEffect(() => {
        setTimeout(() => {
            setPayloadVals(true);
        }, 8000);
    }, []);

    return (
        <>
        { (!payloadVals) 
        ? <div className={'hero'}>
            <div className="skeletonWrapper" style={{gridColumn: '1 / 3'}}>
                <span className={'skeletonWrapper__title'}></span>
                <span className={'skeletonWrapper__text'}></span>
                <span className={'skeletonWrapper__text'}></span>
                <span className={'skeletonWrapper__text'}></span>
                <span className={'skeletonWrapper__text'}></span>
                <span className={'skeletonWrapper__button'}></span>
            </div>
            <div className="skeletonWrapper azDFlexCC">
                <span className={'skeletonWrapper__img'}></span>
                <span className={'skeletonWrapper__img'}></span>
            </div>
        </div>
        : <div className={'hero'}>
            <div className="hero__desc">
                <h1>Project Title</h1>
                <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Architecto dicta delectus beatae reprehenderit iusto suscipit ducimus dolore?</p>
                <button>Scroll Down</button>
            </div>
            <div className="hero__imgs">
                <img src='https://upload.wikimedia.org/wikipedia/commons/thumb/a/a7/React-icon.svg/512px-React-icon.svg.png' alt='img' />
                <img src='https://upload.wikimedia.org/wikipedia/commons/thumb/a/a7/React-icon.svg/512px-React-icon.svg.png' alt='img' />
            </div>
        </div>
        }
        </>
    )
}

export default Hero

