import React, { useEffect } from 'react';
import '../styles/SkeletonContainer.css';
import DataItem from './DataItem';
import { db } from '../DummyData';


function SkeletonContainer({navType, animationType, bothListener}) {

    useEffect(() => {
        if( document.querySelector('.App') ){ 
            console.log( document.querySelectorAll('.DataItem') );
            console.log('yeees'); 
        }
    }, []);

    const direction = ['top', 'right', 'bottom', 'left']; // ln - 1 , i , ln - 1 , 
    const rotateDirection = ['top-left', 'top-right', 'bottom-left', 'bottom-right'];
    const ltspDirec = ['left', 'right', 'left', 'right']
    return (
        <div className={'skeletonContainer'}>{
            db.map( (item, i) => { return(
                //  (i < 4) &&  
                <DataItem  key={i} className={'skeletonContainer__item'} 
                navType={navType} payload={item} animationType = {animationType} 
                bothListener={bothListener} direc={direction[i]} rotDirec={rotateDirection[i]} ltspDirec={ltspDirec[i]}/>
                // style={{ width: '23.5%', boxShadow: 'rgba(0, 0, 0, 0.1) 0px 3px 8px', backgroundColor: '#d2d2d2', borderRadius: '0.5rem' }}/>
            ) } )
        }</div>
    )
}

export default SkeletonContainer
