import React from 'react';
import '../styles/AnimationContainer.css';
import {Fade, Flip, Rotate, Zoom, Bounce, Slide, Roll, LightSpeed, Reveal, reactReveal} from 'react-reveal';
import reactDom from 'react-dom';
// import Reveal from 'react-reveal/Reveal';
// import reactReveal from 'react-reveal/Reveal';
/*animationDirections.map( (animationDirection, i) => { return(
    // <div key={i} className={'animationContainer__item'}>{animationDirection}</div>
    // <Fade key={i} className={'animationContainer__item'} animationDirection>{animationDirection}</Fade>
    // <Fade key={i} className={'animationContainer__item'} left>{animationDirection}</Fade>
    // <Reveal key={i} className={'animationContainer__item'} effect='fadeInUp'>{animationDirection}</Reveal>
    // <Reveal key={i} effect="fadeInUp">     {animationDirection}    </Reveal>
) } )*/

function AnimationContainer({animationDirections, animationType}) {
    return (
        <div className={'animationContainer'}>{
            (animationType === 'Fade') &&
            <>
            <Fade className={'animationContainer__item'} top> Top </Fade>
            <Fade className={'animationContainer__item'} right> Right </Fade>
            <Fade className={'animationContainer__item'} bottom> Bottom </Fade>
            <Fade className={'animationContainer__item'} left> Left </Fade>            
            </>

        }</div>
    )
}

export default AnimationContainer
