import React, { useEffect, useMemo, useState } from 'react';
import AnimationContainer from './components/AnimationContainer';
import AppNav from './components/AppNav';
import Hero from './components/Hero';
import ReactRevelaMenu from './components/ReactRevelaMenu';

function App() {

  const animationDirections = ['top', 'right', 'bottom', 'left'];
  const [anmyType, setAnmyType] = useState('Roll');

  return (
    <div className="App">
      <Hero />
      <AppNav />
      <ReactRevelaMenu setAnmyType={setAnmyType}/>
      <AnimationContainer animationDirections={animationDirections}  animationType = {anmyType}/>
    </div>
  );
}

export default App;
