import React from 'react';
import '../styles/AnimationContainer.css';
import {Fade, Flip, Rotate, Zoom, Bounce, Slide, Roll, LightSpeed} from 'react-reveal';
        // Reveal, reactReveal} from 'react-reveal';
        // import Jump from 'react-reveal/Jump';
        // import Flash from 'react-reveal/Flash';
        // import HeadShake from 'react-reveal/HeadShake';
        // import Jello from 'react-reveal/Jello';
        // import Pulse from 'react-reveal/Pulse';
        // import RubberBand from 'react-reveal/RubberBand';
        // import Shake from 'react-reveal/Shake';
        // import Spin from 'react-reveal/Spin';
        // import Swing from 'react-reveal/Swing';
        // import Tada from 'react-reveal/Tada';
        // import Wobble from 'react-reveal/Wobble';
        import Reveal from 'react-reveal/Reveal';
// import reactDom from 'react-dom';
// import Reveal from 'react-reveal/Reveal';
// import reactReveal from 'react-reveal/Reveal';
/*animationDirections.map( (animationDirection, i) => { return(
    // <div key={i} className={'animationContainer__item'}>{animationDirection}</div>
    // <Fade key={i} className={'animationContainer__item'} animationDirection>{animationDirection}</Fade>
    // <Fade key={i} className={'animationContainer__item'} left>{animationDirection}</Fade>
    // <Reveal key={i} className={'animationContainer__item'} effect='fadeInUp'>{animationDirection}</Reveal>
    // <Reveal key={i} effect="fadeInUp">     {animationDirection}    </Reveal>
) } )*/

export default function AnimationContainer({animationDirections, animationType}) {
    return (
        <div className={'animationContainer'}>{
            (animationType === 'Fade') &&
            // animationDirections.map( (animationDirection, i) => { return(
                <Reveal effect='fadeInUp'>     Top    </Reveal>
            // ) } )
            /*(animationType === 'Fade') ?
            <>
                <Fade className={'animationContainer__item'} top> Top </Fade>
                <Fade className={'animationContainer__item'} right> Right </Fade>
                <Fade className={'animationContainer__item'} bottom> Bottom </Fade>
                <Fade className={'animationContainer__item'} left> Left </Fade>            
            </>
            :(animationType === 'Flip') ?
            <>
                <Flip className={'animationContainer__item'} top> Top </Flip>
                <Flip className={'animationContainer__item'} right> Right </Flip>
                <Flip className={'animationContainer__item'} bottom> Bottom </Flip>
                <Flip className={'animationContainer__item'} left> Left </Flip>            
            </>
            :(animationType === 'Rotate') ?
            <>
                <Rotate className={'animationContainer__item'} top left> Top Left </Rotate>            
                <Rotate className={'animationContainer__item'} top right> Top Right </Rotate>
                <Rotate className={'animationContainer__item'} bottom left> Bottom Left </Rotate>
                <Rotate className={'animationContainer__item'} bottom right> Bottom Right </Rotate>                
            </>            
            :(animationType === 'Zoom') ?
            <>
                <Zoom className={'animationContainer__item'} top> Top </Zoom>
                <Zoom className={'animationContainer__item'} right> Right </Zoom>
                <Zoom className={'animationContainer__item'} bottom> Bottom </Zoom>
                <Zoom className={'animationContainer__item'} left> Left </Zoom>            
            </>
            :(animationType === 'Bounce') ?
            <>
                <Bounce className={'animationContainer__item'} top> Top </Bounce>
                <Bounce className={'animationContainer__item'} right> Right </Bounce>
                <Bounce className={'animationContainer__item'} bottom> Bottom </Bounce>
                <Bounce className={'animationContainer__item'} left> Left </Bounce>            
            </>
            :(animationType === 'Slide') ?
            <>
                <Slide className={'animationContainer__item'} top> Top </Slide>
                <Slide className={'animationContainer__item'} right> Right </Slide>
                <Slide className={'animationContainer__item'} bottom> Bottom </Slide>
                <Slide className={'animationContainer__item'} left> Left </Slide>            
            </>
            :(animationType === 'Roll') ?
            <>                                
                <Roll className={'animationContainer__item'} top> Top </Roll>
                <Roll className={'animationContainer__item'} right> Right </Roll>
                <Roll className={'animationContainer__item'} bottom> Bottom </Roll>
                <Roll className={'animationContainer__item'} left> Left </Roll>
            </>
            :(animationType === 'LightSpeed') ?
            <>
                <LightSpeed className={'animationContainer__item'} left> Left </LightSpeed>
                <LightSpeed className={'animationContainer__item'} right> Right </LightSpeed>                                
            </>                        
            :(animationType === 'Jump') ? <Jump className={'animationContainer__item'} > Jump </Jump>
            :(animationType === 'Flash') ? <Flash className={'animationContainer__item'} > Flash </Flash>
            :(animationType === 'HeadShake') ? <HeadShake className={'animationContainer__item'} > HeadShake </HeadShake>
            :(animationType === 'Jello') ? <Jello className={'animationContainer__item'} > Jello </Jello>
            :(animationType === 'Pulse') ? <Pulse className={'animationContainer__item'} > Pulse </Pulse>
            :(animationType === 'RubberBand') ? <RubberBand className={'animationContainer__item'} > RubberBand </RubberBand>
            :(animationType === 'Shake') ? <Shake className={'animationContainer__item'} > Shake </Shake>
            :(animationType === 'Spin') ? <Spin className={'animationContainer__item'} > Spin </Spin>
            :(animationType === 'Swing') ? <Swing className={'animationContainer__item'} > Swing </Swing>            
            :(animationType === 'Tada') ? <Tada className={'animationContainer__item'} > Tada </Tada>
            :(animationType === 'Wobble') ? <Wobble className={'animationContainer__item'} > Wobble </Wobble>
            : null*/
        }</div>
    )
}

// export default AnimationContainer
