import React, { useEffect, useState } from 'react';
import '../styles/General.css';
import '../styles/DataItem.css';
import { Fade, Flip } from 'react-reveal';
import Jello from 'react-reveal/Jello';
import DataItemCard from './DataItemCard';

function DataItem({navType, payload, animationType, lisn}) {

    const [bothListener, setBothListener] = useState(lisn);
    useEffect(() => {
        // setBothListener(false);
        setTimeout(() => {
            setBothListener(true);
        }, 8000);
    }, []);

    return (
    <> {

        (navType === 'Both' &&  bothListener === false) ?
        <div className={'skeletonWrapper'}>
            <div style={{ display: 'flex', alignItems: 'center', backgroundColor: 'transparent' }}>
                <span className={'skeletonWrapper__img'}></span>
                <div style={{ width: '7rem' }}>
                    <span className={'skeletonWrapper__text'} style={{ display: 'block' }}></span>
                    <span className={'skeletonWrapper__text'} style={{ display: 'block' }}></span>
                </div>
            </div>
            <span className={'skeletonWrapper__text'}></span>
            <span className={'skeletonWrapper__text'}></span>
            <span className={'skeletonWrapper__text'}></span>
            <span className={'skeletonWrapper__text'}></span>
            <span className={'skeletonWrapper__button'}></span>
        </div>
        : (navType === 'Both' && bothListener === true) ?
            <DataItemCard payload={payload} />
        :null



    /*(navType === 'Skeleton')
    ? <div className={'skeletonWrapper'}>
        <div style={{ display: 'flex', alignItems: 'center', backgroundColor: 'transparent' }}>
            <span className={'skeletonWrapper__img'}></span>
            <div style={{ width: '7rem' }}>
                <span className={'skeletonWrapper__text'} style={{ display: 'block' }}></span>
                <span className={'skeletonWrapper__text'} style={{ display: 'block' }}></span>
            </div>
        </div>
        <span className={'skeletonWrapper__text'}></span>
        <span className={'skeletonWrapper__text'}></span>
        <span className={'skeletonWrapper__text'}></span>
        <span className={'skeletonWrapper__text'}></span>
        <span className={'skeletonWrapper__button'}></span>
    </div>

    : 
        (animationType === 'Fade') ?    
            <Fade right>    <DataItemCard payload={payload} />    </Fade>
        : (animationType === 'Flip') ?    
            <Flip right>    <DataItemCard payload={payload} />  </Flip>
        : <Jello>
            <DataItemCard payload={payload} />
        </Jello>*/

    }</>
    )
}

export default DataItem
