import React from 'react';
import '../styles/AnimationContainer.css';
import {Fade, Flip, Rotate, Zoom, Bounce, Slide, Roll, LightSpeed, Reveal, reactReveal} from 'react-reveal';
import reactDom from 'react-dom';
// import Reveal from 'react-reveal/Reveal';
// import reactReveal from 'react-reveal/Reveal';
/*animationDirections.map( (animationDirection, i) => { return(
    // <div key={i} className={'animationContainer__item'}>{animationDirection}</div>
    // <Fade key={i} className={'animationContainer__item'} animationDirection>{animationDirection}</Fade>
    // <Fade key={i} className={'animationContainer__item'} left>{animationDirection}</Fade>
    // <Reveal key={i} className={'animationContainer__item'} effect='fadeInUp'>{animationDirection}</Reveal>
    // <Reveal key={i} effect="fadeInUp">     {animationDirection}    </Reveal>
) } )*/

function AnimationContainer({animationDirections, animationType}) {
    return (
        <div className={'animationContainer'}>{
            (animationType === 'Fade') ?
            <>
                <Fade className={'animationContainer__item'} top> Top </Fade>
                <Fade className={'animationContainer__item'} right> Right </Fade>
                <Fade className={'animationContainer__item'} bottom> Bottom </Fade>
                <Fade className={'animationContainer__item'} left> Left </Fade>            
            </>
            :(animationType === 'Flip') ?
            <>
                <Flip className={'animationContainer__item'} top> Top </Flip>
                <Flip className={'animationContainer__item'} right> Right </Flip>
                <Flip className={'animationContainer__item'} bottom> Bottom </Flip>
                <Flip className={'animationContainer__item'} left> Left </Flip>            
            </>
            :(animationType === 'Zoom') ?
            <>
                <Zoom className={'animationContainer__item'} top> Top </Zoom>
                <Zoom className={'animationContainer__item'} right> Right </Zoom>
                <Zoom className={'animationContainer__item'} bottom> Bottom </Zoom>
                <Zoom className={'animationContainer__item'} left> Left </Zoom>            
            </>
            :(animationType === 'Bounce') ?
            <>
                <Bounce className={'animationContainer__item'} top> Top </Bounce>
                <Bounce className={'animationContainer__item'} right> Right </Bounce>
                <Bounce className={'animationContainer__item'} bottom> Bottom </Bounce>
                <Bounce className={'animationContainer__item'} left> Left </Bounce>            
            </>
            :(animationType === 'Slide') ?
            <>
                <Slide className={'animationContainer__item'} top> Top </Slide>
                <Slide className={'animationContainer__item'} right> Right </Slide>
                <Slide className={'animationContainer__item'} bottom> Bottom </Slide>
                <Slide className={'animationContainer__item'} left> Left </Slide>            
            </>
            :(animationType === 'Roll') ?
            <>
                <Roll className={'animationContainer__item'} top> Top React Reveal</Roll>
                <Roll className={'animationContainer__item'} right> Right React Reveal</Roll>
                <Roll className={'animationContainer__item'} bottom> Bottom React Reveal</Roll>
                <Roll className={'animationContainer__item'} left> Left React Reveal</Roll>            
            </>
            :
            <>
                <Roll className={'animationContainer__item'} top> Top </Roll>
                <Roll className={'animationContainer__item'} right> Right </Roll>
                <Roll className={'animationContainer__item'} bottom> Bottom </Roll>
                <Roll className={'animationContainer__item'} left> Left </Roll>            
            </>


        }</div>
    )
}

export default AnimationContainer
