// import logo from './logo.svg';
// import './App.css';
import React, { useState } from 'react';
import reactDom from 'react-dom';
import AnimationContainer from './components/AnimationContainer';
import Hero from './components/Hero';
import './styles/Menus.css';

function App() {


  const animationTypes = ['Fade', 'Flip', 'Rotate', 'Zoom', 'Bounce', 'Slide', 'Roll', 'LightSpeed'];
  const animationDirections = ['top', 'right', 'bottom', 'left'];

  // const [anmyType, setAnmyType] = useState((type) => {
  //   return React.createElement( type, {}, 'wooow' );
  // })

  const [anmyType, setAnmyType] = useState('Fade');

  return (
    <div className="App">
      <Hero />

      <div className={'ReactRevelaMenu'}>{
        animationTypes.map( (animationType, i) => {return(
          <div key={i} className={`ReactRevelaMenu__item ${animationType}`}
          onClick={() => setAnmyType(`${animationType}`)}
          >{animationType}</div>
        )})
      }</div>

      <AnimationContainer animationDirections={animationDirections}  animationType = {anmyType}/>

    </div>
  );
}

export default App;
