import React, { useEffect, useState } from 'react';
import '../styles/Menus.css';
import '../styles/General.css';

export default function ReactRevelaMenu({setAnmyType}) {
    const [animationTypes1, setAnimationTypes1] = useState([]);
    const [animationTypes2, setAnimationTypes2] = useState([]);

    useEffect(() => {
      setTimeout(() => {
        setAnimationTypes1(['Fade', 'Flip', 'Rotate', 'Zoom', 'Bounce', 'Slide', 'Roll', 'LightSpeed']);
        setAnimationTypes2(['Jump', 'Flash', 'HeadShake', 'Jello', 'Pulse', 'RubberBand', 'Shake', 'Spin', 'Swing', 'Tada', 'Wobble']);
      }, 8000);
    }, []);

    return (
      <> 
      {
        (animationTypes1.length === 0) ?
        <>
        <div className="skeletonWrapper azDFlexCSevn" style={{boxShadow: 'rgba(0, 0, 0, 0.1) 0px 3px 8px'}}>
          <span className={'skeletonWrapper__menu'}></span><span className={'skeletonWrapper__menu'}></span>
          <span className={'skeletonWrapper__menu'}></span><span className={'skeletonWrapper__menu'}></span>
          <span className={'skeletonWrapper__menu'}></span><span className={'skeletonWrapper__menu'}></span>
          <span className={'skeletonWrapper__menu'}></span><span className={'skeletonWrapper__menu'}></span>
        </div>
        <div className="skeletonWrapper azDFlexCSevn" style={{flexWrap: 'wrap', boxShadow: 'rgba(0, 0, 0, 0.1) 0px 3px 8px'}}>
          <span className={'skeletonWrapper__menu'}></span><span className={'skeletonWrapper__menu'}></span>
          <span className={'skeletonWrapper__menu'}></span><span className={'skeletonWrapper__menu'}></span>
          <span className={'skeletonWrapper__menu'}></span><span className={'skeletonWrapper__menu'}></span>
          <span className={'skeletonWrapper__menu'}></span><span className={'skeletonWrapper__menu'}></span>
          <span className={'skeletonWrapper__menu'}></span>
        </div>
        </>
        :
        <>
          <div className={'ReactRevelaMenu'}>{
            animationTypes1.map( (animationType, i) => {return(
              <div key={i} className={`ReactRevelaMenu__item ${animationType}`}
              onClick={() => setAnmyType(`${animationType}`)}
              >{animationType}</div>
            )})
          }</div>
          <div className={'ReactRevelaMenu'}>{
            animationTypes2.map( (animationType, i) => {return(
              <div key={i} className={`ReactRevelaMenu__item ${animationType}`}
              onClick={() => setAnmyType(`${animationType}`)}
              >{animationType}</div>
            )})
          }</div>
        </>
      }
      </>
    )
}

