import React from 'react';
import '../styles/General.css';
import '../styles/DataItem.css';
import {Fade, Flip, Rotate, Zoom, Bounce, Slide, Roll, LightSpeed} from 'react-reveal';
import Jump from 'react-reveal/Jump';
import Flash from 'react-reveal/Flash';
import HeadShake from 'react-reveal/HeadShake';
import Jello from 'react-reveal/Jello';
import Pulse from 'react-reveal/Pulse';
import RubberBand from 'react-reveal/RubberBand';
import Shake from 'react-reveal/Shake';
import Spin from 'react-reveal/Spin';
import Swing from 'react-reveal/Swing';
import Tada from 'react-reveal/Tada';
import Wobble from 'react-reveal/Wobble';
import DataItemCard from './DataItemCard';

function DataItem({navType, payload, animationType, bothListener}) {

    const handelItem = () => {
        if( (navType === 'Both' &&  bothListener === false)  || navType === 'Skeleton'){  return(
        <div className={'skeletonWrapper'}>
            <div style={{ display: 'flex', alignItems: 'center', backgroundColor: 'transparent' }}>
                <span className={'skeletonWrapper__img'}></span>
                <div style={{ width: '7rem' }}>
                    <span className={'skeletonWrapper__text'} style={{ display: 'block' }}></span>
                    <span className={'skeletonWrapper__text'} style={{ display: 'block' }}></span>
                </div>
            </div>
            <span className={'skeletonWrapper__text'}></span>
            <span className={'skeletonWrapper__text'}></span>
            <span className={'skeletonWrapper__text'}></span>
            <span className={'skeletonWrapper__text'}></span>
            <span className={'skeletonWrapper__button'}></span>
        </div>
        )} else if( (navType === 'Both' && bothListener === true)  || navType === 'ReactReveal'){
            if(animationType === 'Fade') {return(        <Fade right>    <DataItemCard payload={payload} />  </Fade> )
            } else if(animationType === 'Flip') {return(       <Flip right>    <DataItemCard payload={payload} />  </Flip> )
            } else if(animationType === 'Rotate') {return(     <Rotate  top left>   <DataItemCard payload={payload} />   </Rotate> )
            } else if(animationType === 'Zoom') {return(       <Zoom  top>    <DataItemCard payload={payload} />  </Zoom> )
            } else if(animationType === 'Bounce') {return(     <Bounce  top>    <DataItemCard payload={payload} /> </Bounce> )
            } else if(animationType === 'Slide') {return(      <Slide  top>    <DataItemCard payload={payload} />  </Slide> )
            } else if(animationType === 'Roll') {return(       <Roll  top>    <DataItemCard payload={payload} />   </Roll> )
            } else if(animationType === 'LightSpeed') {return( <LightSpeed  left>    <DataItemCard payload={payload} />   </LightSpeed> )
            } else if(animationType === 'Jump') {return(       <Jump>    <DataItemCard payload={payload} />    </Jump> )
            } else if(animationType === 'Flash') {return(      <Flash>     <DataItemCard payload={payload} />  </Flash> )
            } else if(animationType === 'HeadShake') {return(  <HeadShake>   <DataItemCard payload={payload} />   </HeadShake> )
            } else if(animationType === 'Jello') {return(      <Jello>     <DataItemCard payload={payload} />  </Jello> )
            } else if(animationType === 'Pulse') {return(      <Pulse>     <DataItemCard payload={payload} />  </Pulse> )
            } else if(animationType === 'RubberBand') {return( <RubberBand  >     <DataItemCard payload={payload} />   </RubberBand> )
            } else if(animationType === 'Shake') {return(      <Shake>     <DataItemCard payload={payload} />  </Shake> )
            } else if(animationType === 'Spin') {return(       <Spin>    <DataItemCard payload={payload} />   </Spin> )
            } else if(animationType === 'Swing') {return(      <Swing>     <DataItemCard payload={payload} /> </Swing> )
            } else if(animationType === 'Tada') {return(       <Tada>    <DataItemCard payload={payload} />    </Tada> )
            } else if(animationType === 'Wobble') {return(     <Wobble>     <DataItemCard payload={payload} />  </Wobble> )
            } else{  return( <Jello>   <DataItemCard payload={payload} />  </Jello> )   }
        }else{ return(null) }


    }

    return (
    <> {
        handelItem
        /*( (navType === 'Both' &&  bothListener === false)  || navType === 'Skeleton') ?
        <div className={'skeletonWrapper'}>
            <div style={{ display: 'flex', alignItems: 'center', backgroundColor: 'transparent' }}>
                <span className={'skeletonWrapper__img'}></span>
                <div style={{ width: '7rem' }}>
                    <span className={'skeletonWrapper__text'} style={{ display: 'block' }}></span>
                    <span className={'skeletonWrapper__text'} style={{ display: 'block' }}></span>
                </div>
            </div>
            <span className={'skeletonWrapper__text'}></span>
            <span className={'skeletonWrapper__text'}></span>
            <span className={'skeletonWrapper__text'}></span>
            <span className={'skeletonWrapper__text'}></span>
            <span className={'skeletonWrapper__button'}></span>
        </div>

        : ( (navType === 'Both' && bothListener === true)  || navType === 'ReactReveal') ?
            (animationType === 'Fade') ?        <Fade right>    <DataItemCard payload={payload} />  </Fade>
            :(animationType === 'Flip') ?       <Flip right>    <DataItemCard payload={payload} />  </Flip>
            :(animationType === 'Rotate') ?     <Rotate  top left>   <DataItemCard payload={payload} />   </Rotate>
            :(animationType === 'Zoom') ?       <Zoom  top>    <DataItemCard payload={payload} />  </Zoom>
            :(animationType === 'Bounce') ?     <Bounce  top>    <DataItemCard payload={payload} /> </Bounce>
            :(animationType === 'Slide') ?      <Slide  top>    <DataItemCard payload={payload} />  </Slide>
            :(animationType === 'Roll') ?       <Roll  top>    <DataItemCard payload={payload} />   </Roll>
            :(animationType === 'LightSpeed') ? <LightSpeed  left>    <DataItemCard payload={payload} />   </LightSpeed>
            :(animationType === 'Jump') ?       <Jump>    <DataItemCard payload={payload} />    </Jump>
            :(animationType === 'Flash') ?      <Flash>     <DataItemCard payload={payload} />  </Flash>
            :(animationType === 'HeadShake') ?  <HeadShake>   <DataItemCard payload={payload} />   </HeadShake>
            :(animationType === 'Jello') ?      <Jello>     <DataItemCard payload={payload} />  </Jello>
            :(animationType === 'Pulse') ?      <Pulse>     <DataItemCard payload={payload} />  </Pulse>
            :(animationType === 'RubberBand') ? <RubberBand  >     <DataItemCard payload={payload} />   </RubberBand>
            :(animationType === 'Shake') ?      <Shake>     <DataItemCard payload={payload} />  </Shake>
            :(animationType === 'Spin') ?       <Spin>    <DataItemCard payload={payload} />   </Spin>
            :(animationType === 'Swing') ?      <Swing>     <DataItemCard payload={payload} /> </Swing>
            :(animationType === 'Tada') ?       <Tada>    <DataItemCard payload={payload} />    </Tada>
            :(animationType === 'Wobble') ?     <Wobble>     <DataItemCard payload={payload} />  </Wobble>
            : <Jello>   <DataItemCard payload={payload} />  </Jello>
        
        :null*/
    }</>
    )
}

export default DataItem
