import React, { useEffect, useMemo, useState } from 'react';
import reactDom from 'react-dom';
import AnimationContainer from './components/AnimationContainer';
import AppNav from './components/AppNav';
import Hero from './components/Hero';
import ReactRevelaMenu from './components/ReactRevelaMenu';
import SkeletonContainer from './components/SkeletonContainer';
import './styles/General.css';
function App() {

  const animationDirections = ['top', 'right', 'bottom', 'left'];
  const [anmyType, setAnmyType] = useState('Roll');
  const [navType, setNavType] = useState('Skeleton');
  const [data, setData] = useState(null);

  useEffect(() => {
    // setTimeout( async () => {
      const unsub =  async() => {
      const res = await fetch('https://jsonplaceholder.typicode.com/posts');
      const db = await res.json();
      setData(db);
      }
      return unsub;
    // }, 2500);
  }, []);

  return (
    <div className="App">
      <Hero />
      <AppNav setNavType={setNavType}/>
      <div className={`appMain ${navType}`}>
        <ReactRevelaMenu setAnmyType={setAnmyType}/>
        {/* <AnimationContainer animationDirections={animationDirections}  animationType = {anmyType} /> */}
        <SkeletonContainer navType={navType} data={data}/>
      </div>
    </div>
  );
}

export default App;
